body.script:
    document.body.classList.remove('noscript');

    function checkIfGrLibraryLoaded() {
        return ('undefined' !== typeof gr) && (null !== gr);
	}

	if (checkIfGrLibraryLoaded() && gr.isLogged()) {
		document.body.classList.add('logged-user');
	}

	if (false) {
		document.body.classList.add('logged-oauth' ,'logged-user');

		if (false) {
			document.body.classList.add('premium-user');
		}

		if (false) {
			document.body.classList.add('essential-user');
		}
	}

    if (checkIfGrLibraryLoaded() && gr.isPremium()) {
        document.body.classList.add('premium-user');
    }

    if (checkIfGrLibraryLoaded() && gr.isEssential()) {
        document.body.classList.add('essential-user');
    }

    if (checkIfGrLibraryLoaded() && gr.user && gr.user.email.match(/\@freepik.com/)) {
        document.body.classList.add('admin-user');
	}

	if ('undefined' !== typeof CampaignHelpers && CampaignHelpers.showBanner()) {
		document.body.classList.add('campaign-active');
	}

div.script:
if ('undefined' !== typeof Showcase) {
	Showcase.setMulti('');
	Showcase.setLayout({
		visibilityUpdated: document.querySelector('.detail__related') ? true : false,
	});

	if ('undefined' !== typeof Lazyload) {
		Lazyload.run();
	}
}

[].slice.call(document.querySelectorAll('.avatar img'))
	.forEach(function(img) {
		var image = new Image();
		image.onerror = function() {
			img.src = randomAvatar.apply(null, [img.alt]);
		}
		image.src = img.src;
	});
